import React from 'react';

const mapStateToProps = state => ({
  filters: state.filters
});

const mapDispatchToProps = dispatch => ({
  toggleFilter: location => dispatch(actions.toggleFilter(location))
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(function Filter(props) {
  const { location, filters, onClick } = props;
  const className =
    `CompanyList__Filter ${filters.includes(location) ? 'CompanyList__Filter--active' : ''}`;

  return (
    <a
      href="#ESLintComplainsALot"
      {...{className}}
      {...{onClick}}>

        {location}

    </a>
  );
});
